function unlockNextArticle(currentArticleId: number) {
  const allArticles = constitutionData.flatMap((title) => title.articles)
  const currentIndex = allArticles.findIndex((a) => a.id === currentArticleId)

  if (currentIndex !== -1 && currentIndex < allArticles.length - 1) {
    const nextArticle = allArticles[currentIndex + 1]
    nextArticle.available = true
  }
}

export function getNextAvailableArticle(): Article | null {
  const allArticles = constitutionData.flatMap((title) => title.articles)
  return allArticles.find((article) => article.available && !article.completed) || null
}

export function loadProgressFromStorage() {
  if (typeof window === "undefined") return

  import("./storage").then(({ loadUserProgress }) => {
    const progress = loadUserProgress()
    if (!progress) return

    const allArticles = constitutionData.flatMap((title) => title.articles)

    // Sync localStorage data with in-memory constitution data
    allArticles.forEach((article) => {
      const savedProgress = progress.articles[article.id]
      if (savedProgress) {
        article.completed = savedProgress.completed
        article.available = savedProgress.available
        article.attempts = savedProgress.attempts
        article.correctAnswers = savedProgress.correctAnswers
      }
    })

    // Ensure first article is always available
    if (allArticles.length > 0) {
      allArticles[0].available = true
    }
  })
}

export function initializeProgressState() {
  // Make first article available by default
  const allArticles = constitutionData.flatMap((title) => title.articles)
  if (allArticles.length > 0) {
    allArticles[0].available = true
  }

  // Load any existing progress from localStorage
  loadProgressFromStorage()
}
